import 'bootstrap/dist/css/bootstrap.min.css';
import { over } from 'lodash-es';
import React, {useState,useEffect} from 'react'
import { Dropdown } from 'react-bootstrap';
//import CardData from './cardData';
//import styled from 'styled-components';
//import { DropDownList } from "@progress/kendo-react-dropdowns";
//import { Dropdown } from 'semantic-ui-react'


function DropdownExampleSearchSelection(){


  const [overview,setOverview]=useState(["CSK"]);
    useEffect(() => {
      fetch("/api/getTeams").then(resp => resp.json())
      .then(res => 
        setOverview(res.team))
    }, [])
  const style = {
      display:"flex",
      color: "red",
      fontSize: "10px",
      textAlign: "center",
      padding:"0 1rem",
      height:"100px",
      width:"50px"
  };
  /*CardData(team){
    const [card,setCard]=useState("");
    const URL='/api/getTeamData/'+String(team);
    useEffect(() => {
      fetch(URL).then(resp => resp.json())
      .then(res => 
        console.log(res))
    }, [])
  }*/

  const [value, setValue] = React.useState('Select Team');
  const handleChange = (selection) => {
    setValue(selection);
    const URL='/api/getTeamData/'+String(selection);
    useEffect(() => {
      fetch(URL).then(resp => resp.json())
      .then(res => 
        console.log(res))
    }, [])
  };  


  return(
    <Dropdown style={style}>
      <Dropdown.Toggle variant="success" id="dropdown-basic">
        {value}
      </Dropdown.Toggle>

      <Dropdown.Menu>
      {overview.map(over => (
          <Dropdown.Item eventKey={over} value={over} onSelect={handleChange}>{over}</Dropdown.Item>
          ))}
      </Dropdown.Menu>

    </Dropdown>
  )
}


export default DropdownExampleSearchSelection
