import 'bootstrap/dist/css/bootstrap.min.css';
import { over } from 'lodash-es';
import React, {useState,useEffect} from 'react'
import { Dropdown } from 'react-bootstrap';
//import styled from 'styled-components';
//import { DropDownList } from "@progress/kendo-react-dropdowns";
//import { Dropdown } from 'semantic-ui-react'
function DropdownExampleSearchSelection(){


  const [overview,setOverview]=useState(["CSK"]);
    useEffect(() => {
      fetch("/api/getTeams").then(resp => resp.json())
      .then(res => 
        setOverview(res.team))
    }, [])
  const style = {
      display:"flex",
      color: "red",
      fontSize: "10px",
      textAlign: "center",
      padding:"0 1rem",
      height:"200px"
  };
  const [value, setValue] = React.useState('Select Team');
  const handleChange = (event) => {

    setValue(event)
    console.log(event)
  };  


  return(
    <Dropdown style={style}>
      <Dropdown.Toggle variant="success" id="dropdown-basic">
        {value}
      </Dropdown.Toggle>

      <Dropdown.Menu>
      {overview.map(over => (
          <Dropdown.Item eventKey={over} value={over} onSelect={handleChange}>{over}</Dropdown.Item>
          ))}
      </Dropdown.Menu>

    </Dropdown>
  )
}


export default DropdownExampleSearchSelection
