import 'bootstrap/dist/css/bootstrap.min.css';
import { over } from 'lodash-es';
import React, {useState,useEffect} from 'react'
import { Dropdown } from 'react-bootstrap';
//import CardData from './cardData';
//import styled from 'styled-components';
//import { DropDownList } from "@progress/kendo-react-dropdowns";
//import { Dropdown } from 'semantic-ui-react'



function DropdownExampleSearchSelection(){


  const [overview,setOverview]=useState(["CSK"]);
    useEffect(() => {
      fetch("/api/getTeams").then(resp => resp.json())
      .then(res => 
        setOverview(res.team))
    }, [])
  const style = {
      display:"flex",
      color: "red",
      fontSize: "10px",
      textAlign: "center",
      padding:"0 1rem",
      height:"100px",
      width:"50px"
  };


  function CardData(team) {
    fetch(URL).then(resp => resp.json())
    .then(res => 
      setTest(res))
  }


  const [test,setTest]=useState("Deccan Chargers");
  useEffect(() => {
      const URL = '/api/getTeamData/'+encodeURIComponent("Deccan Chargers");
      fetch(URL).then(resp => resp.json())
        .then(res => 
          setTest(res))
      }, []);

  const [value, setValue] = React.useState('Select Team');
  const handleChange = (selection) => {
    setValue(selection);
    CardData(selection);
  };  


  return(
    <div>
    <Dropdown style={style}>
      <Dropdown.Toggle variant="success" id="dropdown-basic">
        {value}
      </Dropdown.Toggle>
      <Dropdown.Menu>
      {overview.map(over => (
          <Dropdown.Item eventKey={over} value={over} onSelect={handleChange}>{over}</Dropdown.Item>
          ))}
      </Dropdown.Menu>
    </Dropdown>
    <table class="table table-bordered">
    <thead class="thead-dark">
      <tr>
        <th scope="col">Id</th>
        <th scope="col">Team</th>
        <th scope="col">Against</th>
        <th scope="col">Winner</th>
      </tr>
    </thead>
    <tbody>    
      {test.map(te => (
        <tr key={te.id}>
          <td>{te.id}</td>
          <td>{te.team1}</td>
          <td>{te.team2}</td>
          <td>{te.winner}</td>
        </tr>
      ))}
    </tbody>
    </table>
    </div>

  )
}


export default DropdownExampleSearchSelection
