import React, {useState} from 'react'
import styled from 'styled-components'

const Navbar = () => {
    const [isOpen, setIsOpen] = useState(false)
    return (
        <Nav>
            <Logo href="">
            IPL<span>Stats</span>
            </Logo>
            <Hamburger onClick={() => setIsOpen(!isOpen)}>
                <span />
                <span />
                <span />
            </Hamburger>
            <Menu isOpen={isOpen}>
                <Menulink href="">Item1</Menulink>
                <Menulink href="">Item2</Menulink>
                <Menulink href="">Item3</Menulink>
                <Menulink href="">Item4</Menulink>
            </Menu>
        </Nav>
    )
}

const Nav = styled.div`
    padding: 0 2rem;
    display: flex;
    justify-content:space-between;
    align-items:center;
    flex-wrap: wrap;
    background: white;
`;
const Logo = styled.a`
    padding: 1rem 0;
    color: #7b7fda;
    text-decoration: none;
    font-weight:800 ;
    font-size:1.7rem ;
    span {
        font-weight:300;
        font-size: 1.3rem;
    }
`;
const Hamburger = styled.div`
    display:flex;
    flex-direction:column;
    cursor:pointer;
    span{
        height:2px;
        width:25px;
        background:#7b7fda;
        margin-bottom:4px;
        border-radius:5px;
    }
    @media(max-width:768px){
        display:flex;
    }
`;
const Menu = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    position: relative;

    @media(max-width:768px){
        overflow:hidden;
        flex-direction:column;
        width:100%;
        max-height: ${({isOpen}) => (isOpen ? "300px":"0")};
        transition: maxheight 0.3s ease-in;
    }
`;
const Menulink = styled.a`
    padding:1rem 3rem;
    cursor:pointer;
    text-align:center;
    text-decoration:none;
    color:#67bc98;
    &:hover {
        color:#7b7fda;
    }
`;

export default Navbar
